targets.query:
from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["host"] == "$host")
  |> filter(fn: (r) => r["_measurement"] =~ /scale\.truenas\.truenas_disk_stats\.busy\._serial_lunid_[^_]*_[^_]*\.busy/)
  |> map(
        fn: (r) => ({r with _field: r._measurement}),
    )
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> yield(name: "mean")

from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["host"] == "$host")
  |> filter(fn: (r) => r["_measurement"] == "scale.truenas.net.${interface}.received" or r["_measurement"] == "scale.truenas.net.${interface}.sent")
  |> map(
        fn: (r) => ({r with _field: r._measurement}),
    )
  |> aggregateWindow(every: v.windowPeriod, fn: last, createEmpty: false)
  |> yield(name: "last")

from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["host"] == "$host")
  |> filter(fn: (r) => r["_measurement"] =~ /scale\.truenas\.cputemp\.temperatures\.cpu[0-9]+/)
  |> map(
        fn: (r) => ({r with _field: r._measurement}),
    )
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> yield(name: "mean")
